example.programlisting:
foreach(range(0, 9) as $numero) {
    echo $numero;
}
foreach(range(0, 100, 10) as $numero) {
    echo $numero;
}
foreach(range('a', 'z') as $lettera) {
    echo $lettera;
}
foreach(range('z', 'a') as $lettera) {
    echo $lettera;
}

# array_reverse può essere udata per invertire l'ordine di un range
foreach(array_reverse(range(0,9)) as $numero) {
   	echo $numero;
}

#array_map() puà essere usata per convertire gli interi in caratteri usando chr()
foreach(array_map('chr', range(ord('a'),ord('z'))) as $carattere) {
    echo $carattere;
}
