refentry.refsect1:
&reftitle.parameters;




   result



     Risorsa set di risultati di SQLite. Questo parametro non Ã¨ richiesto nella versione
     ad oggetti.

    &sqlite.no-unbuffered;

  &reftitle.examples;



    Esempio procedurale


<?php
$db = sqlite_open('mysqlitedb');
$result = sqlite_query($db, "SELECT * FROM mytable WHERE name='John Doe'");
$rows = sqlite_num_rows($result);

echo "Numero di righe: $rows";
?>







    Esempio ad oggetti


<?php
$db = new SQLiteDatabase('mysqlitedb');
$result = $db->query("SELECT * FROM mytable WHERE name='John Doe'");
$rows = $result->numRows();

echo "Numero di righe: $rows";
?>
